mapper.select:
select aa.*
from ap_article aa
left join ap_article_config aap on aap.article_id = aa.id

    and aap.is_delete != 1
    and aap.is_down != 1

        and aa.publish_time < #{dto.minBehotTime}


        and aa.publish_time > #{dto.maxBehotTime}


        and aa.channel_id = #{dto.tag}


order by aa.publish_time desc
limit #{dto.size}
